mapper.select:
SELECT user_id, user_name, login_name, phone, role_name, u.role_id, u.del_flag FROM sys_user u LEFT JOIN sys_role r ON u.role_id = r.role_id WHERE (u.role_id = #{roleId} OR '' = #{roleId}) AND (r.role_id = #{roleId} OR '' = #{roleId}) AND u.del_Flag = #{delFlag} AND (u.user_name LIKE CONCAT('%', #{userInfo}, '%') OR u.login_name LIKE CONCAT('%', #{userInfo}, '%') OR u.phone LIKE CONCAT('%', #{userInfo}, '%'))
AND u.role_id IS NOT NULL
AND u.parkinglot_id = #{parkingLotId}

SELECT COUNT(*)
FROM sys_user
WHERE login_name = #{loginName}

SELECT COUNT(*)
FROM sys_user
WHERE phone = #{phone}

mapper.update:
UPDATE sys_user
SET user_name = #{userName}, phone = #{phone}, role_id = #{roleId}, is_sysAdmin = #{isAdmin}
WHERE user_id = #{userId}

UPDATE sys_user
SET login_pwd = #{loginPwd}
WHERE user_id = #{userId}

UPDATE sys_user
SET del_flag = #{delFlag}
WHERE user_id = #{userId}
